class _MethodChannelPageState extends State<MethodChannelPage> {
  // 
  MethodChannel methodsChannel = MethodChannel("com.chicdeals/methodsChannel");
  String _langText = '';

  @override
  Widget build(BuildContext context) {
    return Scaffold (
      backgroundColor: Color(0xFFFFFFFF),
      body: Align(
